// Get the data from the lookup result
const currentRow = $input.first().json;

// Get current message count from column B (if it doesn't exist, start at 0)
const currentCount = $input.first().json['Message Counter'] || 0;

// Add 1 to the count
const newCount = parseInt(currentCount) + 1;

// Return the data we need for the next step
return {
  json: {
    session_id: currentRow.A,
    message_count: newCount,
    row_number: currentRow.__rowNum
  }
};
