// Build dynamic keyboard based on message type
const data = items[0].json;

// Prepare base response data
let chatId, messageId, userName, responseText, keyboard, isCallback = false;
let apiMethod = 'sendMessage';

if (data.message) {
  // Handle regular message
  const message = data.message;
  chatId = message.chat.id;

  // Extract user's actual name from Telegram data
  const firstName = message.from.first_name || '';
  const lastName = message.from.last_name || '';
  userName = firstName;
  if (lastName) {
    userName += ` ${lastName}`;
  }
  if (!userName) {
    userName = 'User';
  }

  const text = (message.text || '').toLowerCase();

  // Determine response based on message
  if (text.includes('help')) {
    responseText = '📚 <b>Help Menu</b>\n\nAvailable commands:\n• /start - Main menu\n• /help - This help\n• /rate - Rate features\n\nUse the buttons below!';
    keyboard = [
      [{ text: '🔙 Back to Main', callback_data: 'main' }]
    ];
  } else if (text.includes('settings')) {
    responseText = '⚙️ <b>Settings Menu</b>\n\nConfigure your preferences:';
    keyboard = [
      [{ text: '🔔 Notifications', callback_data: 'notif' }],
      [{ text: '🌍 Language', callback_data: 'lang' }],
      [{ text: '👤 Profile', callback_data: 'profile' }],
      [{ text: '🔙 Back to Main', callback_data: 'main' }]
    ];
  } else {
    // Default main menu
    responseText = `Welcome ${userName}! 👋\n\nI'm your assistant bot. Please select an option:`;
    keyboard = [
      [
        { text: '🎯 Feature 1', callback_data: 'feature1' },
        { text: '🚀 Feature 2', callback_data: 'feature2' }
      ],
      [
        { text: '⚙️ Settings', callback_data: 'settings' },
        { text: '❓ Help', callback_data: 'help' }
      ],
      [
        { text: '📊 Statistics', callback_data: 'stats' },
        { text: '💬 Feedback', callback_data: 'feedback' }
      ]
    ];
  }

} else if (data.callback_query) {
  // Handle callback query
  const callback = data.callback_query;
  chatId = callback.message.chat.id;
  messageId = callback.message.message_id;

  // Extract user's actual name from callback query
  const firstName = callback.from.first_name || '';
  const lastName = callback.from.last_name || '';
  userName = firstName;
  if (lastName) {
    userName += ` ${lastName}`;
  }
  if (!userName) {
    userName = 'User';
  }

  isCallback = true;
  apiMethod = 'editMessageText';

  const action = callback.data;

  switch(action) {
    case 'feature1':
      responseText = `🎯 <b>Feature 1 - Rating System Demo</b>\n\n${userName}, you've activated Feature 1!\n\nHow would you rate this feature?\n\nPlease select a rating from 1 to 5 stars:`;
      keyboard = [
        [
          { text: '⭐', callback_data: 'rate_1' },
          { text: '⭐⭐', callback_data: 'rate_2' },
          { text: '⭐⭐⭐', callback_data: 'rate_3' },
          { text: '⭐⭐⭐⭐', callback_data: 'rate_4' },
          { text: '⭐⭐⭐⭐⭐', callback_data: 'rate_5' }
        ],
        [
          { text: '💭 Skip Rating', callback_data: 'skip_rating' },
          { text: '🔙 Back to Main', callback_data: 'main' }
        ]
      ];
      break;

    case 'rate_1':
      responseText = `😔 <b>Thank you for your feedback!</b>\n\n${userName}, you rated Feature 1: ⭐ (1 star)\n\nWe're sorry to hear you didn't have a great experience. Your feedback helps us improve!\n\nWould you like to provide additional feedback?`;
      keyboard = [
        [{ text: '💬 Write Feedback', callback_data: 'write_feedback' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'rate_2':
      responseText = `😐 <b>Thank you for your feedback!</b>\n\n${userName}, you rated Feature 1: ⭐⭐ (2 stars)\n\nWe appreciate your honest feedback. We'll work on making this better!\n\nWould you like to tell us more?`;
      keyboard = [
        [{ text: '💬 Write Feedback', callback_data: 'write_feedback' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'rate_3':
      responseText = `🙂 <b>Thank you for your feedback!</b>\n\n${userName}, you rated Feature 1: ⭐⭐⭐ (3 stars)\n\nGood to know! We're always looking to improve.\n\nAnything specific you'd like to share?`;
      keyboard = [
        [{ text: '💬 Share Thoughts', callback_data: 'write_feedback' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'rate_4':
      responseText = `😊 <b>Thank you for your feedback!</b>\n\n${userName}, you rated Feature 1: ⭐⭐⭐⭐ (4 stars)\n\nGreat! We're glad you enjoyed it!\n\nWhat could make it perfect?`;
      keyboard = [
        [{ text: '💡 Suggest Improvement', callback_data: 'write_feedback' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'rate_5':
      responseText = `🎉 <b>Thank you for your feedback!</b>\n\n${userName}, you rated Feature 1: ⭐⭐⭐⭐⭐ (5 stars)\n\nAmazing! We're thrilled you love it!\n\nWould you like to explore more features?`;
      keyboard = [
        [{ text: '🚀 Try Feature 2', callback_data: 'feature2' }],
        [{ text: '📊 View Statistics', callback_data: 'stats' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'skip_rating':
      responseText = `No problem, ${userName}! You can rate features anytime.\n\nWhat would you like to do next?`;
      keyboard = [
        [
          { text: '🚀 Try Feature 2', callback_data: 'feature2' },
          { text: '📊 Statistics', callback_data: 'stats' }
        ],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'write_feedback':
      responseText = `💬 <b>Feedback Form</b>\n\n${userName}, please type your feedback message and send it as a regular message.\n\nOr choose an option below:`;
      keyboard = [
        [{ text: '🐛 Report Bug', callback_data: 'bug_report' }],
        [{ text: '💡 Suggest Feature', callback_data: 'feature_request' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'feature2':
      responseText = `🚀 <b>Feature 2 - Advanced Options</b>\n\n${userName}, welcome to Feature 2!\n\nThis feature includes:\n• Data Analytics\n• Custom Reports\n• Export Options\n\nWhat would you like to do?`;
      keyboard = [
        [{ text: '📈 View Analytics', callback_data: 'analytics' }],
        [{ text: '📄 Generate Report', callback_data: 'report' }],
        [{ text: '⭐ Rate This Feature', callback_data: 'rate_feature2' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'rate_feature2':
      responseText = `🚀 <b>Rate Feature 2</b>\n\n${userName}, how would you rate Feature 2?\n\nSelect your rating:`;
      keyboard = [
        [
          { text: '1️⃣', callback_data: 'f2_rate_1' },
          { text: '2️⃣', callback_data: 'f2_rate_2' },
          { text: '3️⃣', callback_data: 'f2_rate_3' },
          { text: '4️⃣', callback_data: 'f2_rate_4' },
          { text: '5️⃣', callback_data: 'f2_rate_5' }
        ],
        [{ text: '🔙 Back', callback_data: 'feature2' }]
      ];
      break;

    case 'profile':
      responseText = `👤 <b>User Profile</b>\n\nName: ${userName}\nUser ID: ${callback.from.id}\nLanguage: ${callback.from.language_code || 'Not set'}\n\nProfile Settings:`;
      keyboard = [
        [{ text: '✏️ Edit Name', callback_data: 'edit_name' }],
        [{ text: '🌍 Change Language', callback_data: 'lang' }],
        [{ text: '🔙 Back to Settings', callback_data: 'settings' }]
      ];
      break;

    case 'stats':
      responseText = `📊 <b>Your Statistics</b>\n\n${userName}, here are your usage stats:\n\n• Features Used: 2\n• Ratings Given: 1\n• Active Days: 3\n• Total Actions: 15\n\nKeep exploring to unlock achievements!`;
      keyboard = [
        [{ text: '🏆 View Achievements', callback_data: 'achievements' }],
        [{ text: '📈 Detailed Stats', callback_data: 'detailed_stats' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'feedback':
      responseText = `💬 <b>Feedback Center</b>\n\n${userName}, we value your input!\n\nHow can we help you today?`;
      keyboard = [
        [{ text: '⭐ Rate Features', callback_data: 'rate_all' }],
        [{ text: '💡 Suggest Ideas', callback_data: 'feature_request' }],
        [{ text: '🐛 Report Issues', callback_data: 'bug_report' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'help':
      responseText = `📚 <b>Help Menu</b>\n\n${userName}, here's how to use this bot:\n\n• Click buttons to navigate\n• Rate features with 1-5 stars\n• Submit feedback anytime\n• Check your stats regularly\n\nNeed assistance?`;
      keyboard = [
        [{ text: '📖 User Guide', callback_data: 'guide' }],
        [{ text: '❓ FAQ', callback_data: 'faq' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'settings':
      responseText = `⚙️ <b>Settings Menu</b>\n\n${userName}, configure your preferences:`;
      keyboard = [
        [{ text: '🔔 Notifications', callback_data: 'notif' }],
        [{ text: '🌍 Language', callback_data: 'lang' }],
        [{ text: '👤 Profile', callback_data: 'profile' }],
        [{ text: '🔐 Privacy', callback_data: 'privacy' }],
        [{ text: '🔙 Back to Main', callback_data: 'main' }]
      ];
      break;

    case 'notif':
      responseText = '🔔 Notification settings updated successfully!';
      keyboard = [
        [{ text: '🔙 Back to Settings', callback_data: 'settings' }]
      ];
      break;

    case 'lang':
      responseText = '🌍 Select your preferred language:';
      keyboard = [
        [{ text: '🇬🇧 English', callback_data: 'lang_en' }],
        [{ text: '🇪🇸 Español', callback_data: 'lang_es' }],
        [{ text: '🇫🇷 Français', callback_data: 'lang_fr' }],
        [{ text: '🔙 Back to Settings', callback_data: 'settings' }]
      ];
      break;

    case 'main':
    default:
      responseText = `Welcome back ${userName}! 👋\n\nWhat would you like to do?`;
      keyboard = [
        [
          { text: '🎯 Feature 1', callback_data: 'feature1' },
          { text: '🚀 Feature 2', callback_data: 'feature2' }
        ],
        [
          { text: '⚙️ Settings', callback_data: 'settings' },
          { text: '❓ Help', callback_data: 'help' }
        ],
        [
          { text: '📊 Statistics', callback_data: 'stats' },
          { text: '💬 Feedback', callback_data: 'feedback' }
        ]
      ];
      break;
  }
}

// Build the request body based on message type
let requestBody;
if (isCallback) {
  requestBody = {
    chat_id: chatId,
    message_id: messageId,
    text: responseText,
    parse_mode: 'HTML',
    reply_markup: {
      inline_keyboard: keyboard
    }
  };
} else {
  requestBody = {
    chat_id: chatId,
    text: responseText,
    parse_mode: 'HTML',
    reply_markup: {
      inline_keyboard: keyboard
    }
  };
}

// Return data for HTTP Request node
return [{
  json: {
    apiMethod: apiMethod,
    requestBody: requestBody,
    isCallback: isCallback,
    callbackQueryId: data.callback_query ? data.callback_query.id : null
  }
}];
